mapper.insert:
INSERT INTO
    preserve (scenic_name, image_url, create_time, audit)
values (#{preserve.scenicName}, #{preserve.imageUrl},#{preserve.createTime}, 0);

mapper.update:
update preserve set status = 2
where id in

    #{id}

mapper.select:
SELECT COUNT(*)
FROM preserve
WHERE type = 1;

SELECT COUNT(*)
FROM preserve
WHERE scenic_name LIKE "%"#{keyword}"%";

SELECT *
FROM preserve
WHERE scenic_name LIKE "%"#{keyword}"%";
