mapper.insert:
INSERT INTO
    scenic_copy1 (scenic_name, image_url, create_time, audit)
values (#{scenic.scenicName}, #{scenic.imageUrl},#{scenic.createTime}, 0);

mapper.update:
UPDATE scenic
SET status = 1
WHERE id = #{id};

UPDATE scenic
SET status = 0
WHERE id = #{id}

update scenic set status = 2
where id in

    #{id}

mapper.select:
SELECT COUNT(*)
FROM scenic
WHERE status != 2;

SELECT COUNT(*)
FROM scenic_copy1
WHERE status != 2;

SELECT COUNT(*)
FROM scenic
WHERE type = 1;

SELECT COUNT(*)
FROM scenic
WHERE scenic_name LIKE "%"#{keyword}"%";

SELECT id, scenic_name, merchant, price, phone, address, create_time, image_url
FROM scenic
WHERE scenic_name LIKE "%"#{keyword}"%";
